mapper.update:
UPDATE moim_tb
set moim_member_count = moim_member_count + 1
where moim_id = #{moimId}

UPDATE moim_tb
moim_title = #{title}, moim_discription = #{discription}, moim_max_member = #{maxMember}, moim_img_path = #{moimImgPath},
WHERE moim_id = #{moimId}

mapper.select:
SELECT m.moim_id AS moimId, m.moim_title AS moimTitle, m.moim_discription AS moimDiscription, m.moim_member_count AS moimMemberCount, m.moim_max_member AS moimMaxMember, m.district_id AS districtId, d.district_name AS districtName, m.category_id AS categoryId, c.category_name AS categoryName, m.user_id AS userId, u.nick_name AS nickName, m.moim_image_path AS moimImagePath FROM moim_tb m JOIN district_tb d ON m.district_id = d.district_id JOIN category_tb c ON m.category_id = c.category_id JOIN user_tb u ON m.user_id = u.user_id
m.district_id = #{districtId}
AND m.category_id = #{categoryId}
m.category_id = #{categoryId}
AND (m.moim_title LIKE CONCAT('%', #{keyword}, '%') OR m.moim_discription LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY m.moim_id DESC
